struct WhiteRow<Content: View>: View {
    let content: Content
    init(@ViewBuilder content: () -> Content) { self.content = content() }

    var body: some View {
        HStack { content }
            .padding(.horizontal)
            .padding(.vertical, 17)
            .background(Color.white)
            .cornerRadius(50)
            .shadow(color: Color.black.opacity(0.20), radius: 5, x: 0, y: 3)
    }
}

